import React from "react";
import { NavLink } from "react-router-dom";
import { styled } from "styled-components";
import { Button } from "../styles/button";
const HeroSection = () => {
  const Wrapper = styled.section`
    padding: 9rem 0;

    .section-hero-data {
      display: flex;
      flex-direction: column;
      justify-content: center;
    }
    .btn {
      max-width: 16rem;
    }

    .hero-top-data {
      text-transform: uppercase;
      font-weight: 500;
      font-size: 1.5rem;
      color: ${({ theme }) => theme.colors.helper};
    }

    .hero-para {
      margin-top: 1.5rem;
      margin-bottom: 3.4rem;
      max-width: 60rem;
    }

    .hero-heading {
      text-transform: uppercase;
      font-size: 6.4rem;
    }
  `;

  return (
    <Wrapper>
      <div className="container grid grid-two-column">
        <div className="section-hero-data">
          <p className="hero-top-heading">THIS IS ME</p>
          <h1 className="hero-heading">WEB TECHNICAL</h1>
          <p className="hero-para">
            I'm Gurjant singh.A Software Devloper and youtuber.A Software
            Devloper , youtuber and freelancer
          </p>
        </div>

        <Button className="btn hireme-btn">
          <NavLink to="/contact">hire me</NavLink>
        </Button>

        <div className="section-hero-image">
          <picture>
            <img src="./images/hero.png" alt="" srcSet="" />
          </picture>
        </div>
      </div>
    </Wrapper>
  );
};

export default HeroSection;
